from collections import defaultdict
def max_aggregate(stdata):
    temp = defaultdict(int)
    for name, marks in stdata:
        temp[name] += marks

    if not temp:  
        return None
    return max(temp.items(), key=lambda x: x[1] if x[1] > 0 else -1)

